import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

export interface SetupProps {
    previousPlayers: string[];
    setSetupInfo: (info: SetupInfo) => void;
};

export const Setup: React.FC<SetupProps> = ({
    previousPlayers
    , setSetupInfo
}) => {

    const nav = useNavigate();

    const [chosenPlayers, setChosenPlayers] = useState(
        previousPlayers.map(x => ({
            name: x
            , checked: false
        }))
    );

    const togglePlayer = (name: string) => setChosenPlayers(
        chosenPlayers.map(x => ({
            ...x 
            , checked: x.name == name ? !x.checked : x.checked
        }))
    );

    const recordGame = () => {
        setSetupInfo({
            start: new Date().toISOString()
            , chosenPlayers: chosenPlayers
                .filter(x => x.checked)
                .map(x => x.name)
        });
        nav("/finish");
    };

    return (
        <>
            <h2>Setup</h2>
            <p>Add new player/select new player</p>
            <Button 
                variant="primary"
                onClick={recordGame}    
            >
                Record Game
            </Button>{' '}

            <Form className = "mt-5">
                <Form.Group className="mb-3">
                    <Form.Label></Form.Label>
                    <Form.Control />
                    <Button>Add Player</Button>
                </Form.Group>               
                {
                    chosenPlayers.map(x =>(
                        <Form.Check
                            label={x.name}
                            checked={x.checked}
                            onChange={() => togglePlayer(x.name)} 
                        />
                    ))
                }
            </Form>
        </>
    );
};
